from collections import Counter

import requests
from bs4 import BeautifulSoup
from loguru import logger

from enums import Skill

BASE_URL = "https://djinni.co/jobs/keyword-python/?keywords=django&page=1"


def get_last_page_index(data):
    parser = BeautifulSoup(data, "html.parser")
    last_tag = parser.findAll("a", class_="page-link")[-2]
    if not last_tag:
        return 1
    return int(last_tag.text) + 1


def get_page(url):
    response = requests.get(url)
    data = response.text
    return data


def get_all_djinni_links():
    page = get_page(BASE_URL)
    links = []
    for page_index in range(1, 2):
        # for page_index in range(1, get_last_page_index(page)):
        url = BASE_URL[:-1] + str(page_index)
        logger.info("Getting '%s'" % url)
        page = get_page(url)
        parser = BeautifulSoup(page, "html.parser")
        links.extend(parser.findAll("a", class_="profile"))
    return links


